function HourCell({ enabled }) {
    const className = enabled ? "hour hour-enabled" : "hour";
    return (<div className={className}></div>);
}

function TimeLabel({ hour }) {
    return (<div className="timelabel">{hour}h</div>);
}

export default function Timeline() {
    const timelabels = arrayRange(1, 72, 1)
        .map(hour => {
            return (
                <>
                    <TimeLabel hour={hour} />
                </>
            )
        });
    const days = arrayRange(1, 72, 1)
        .map(hour => {
            const dayHour = hour % 24;
            return (
                <>
                    <HourCell enabled={dayHour >= 9 && dayHour < 13 || dayHour >= 14 && dayHour < 18} />
                </>
            )
        });
    return (
        <div className="timeline">
            <div className="board-row">
                {timelabels}
            </div>
            <div className="board-row">
                {days}
            </div>
        </div>
    );
}

const arrayRange = (start, stop, step) =>
    Array.from(
        { length: (stop - start) / step + 1 },
        (value, index) => start + index * step
    );
